import tkinter as tkfrom tkinter import filedialog, messagebox, ttkimport zipfileimport tarfileimport gzipimport lz4.frameimport osdef compress_files():    format_type = format_var.get()    if not format_type:        messagebox.showwarning("Select Format", "Please choose a compression format.")        return    files = filedialog.askopenfilenames(title="Select files to compress")    if not files:        return    # Map extensions and folder names    ext_map = {        "ZIP": ".zip",        "GZIP": ".gz",        "LZ4": "tar.gz.lz4",        "TAR.GZ": ".tar.gz"    }    folder_map = {        "ZIP": "zip",        "GZIP": "gz",        "LZ4": "lz4",        "TAR.GZ": "tar"    }    # Create folder if not exists    target_folder = folder_map[format_type]    if not os.path.exists(target_folder):        os.makedirs(target_folder)    # Build save path inside the folder    archive_name = f"Archive{ext_map[format_type]}"    save_path = os.path.join(target_folder, archive_name)    try:        if format_type == "ZIP":            with zipfile.ZipFile(save_path, 'w', zipfile.ZIP_DEFLATED) as zipf:                for file in files:                    zipf.write(file, os.path.basename(file))        elif format_type == "GZIP":            if len(files) > 1:                messagebox.showwarning("GZIP Limitation", "GZIP supports only one file. Compressing first file.")            with open(files[0], 'rb') as f_in:                with gzip.open(save_path, 'wb') as f_out:                    f_out.writelines(f_in)        elif format_type == "LZ4":            if len(files) > 1:                messagebox.showwarning("LZ4 Limitation", "LZ4 supports only one file. Compressing first file.")            with open(files[0], 'rb') as f_in:                compressed = lz4.frame.compress(f_in.read())            with open(save_path, 'wb') as f_out:                f_out.write(compressed)        elif format_type == "TAR.GZ":            with tarfile.open(save_path, "w:gz") as tar:                for file in files:                    tar.add(file, arcname=os.path.basename(file))        messagebox.showinfo("Success", f"Created {format_type} archive:\n{save_path}")    except Exception as e:        messagebox.showerror("Error", str(e))# GUI Setuproot = tk.Tk()root.title("GoZip v1.1")root.geometry("400x220")root.resizable(False, False)font_style = ("Verdana", 11)tk.Label(root, text="Choose Compression Format:", font=font_style).pack(pady=10)format_var = tk.StringVar()format_dropdown = ttk.Combobox(root, textvariable=format_var, values=["ZIP", "GZIP", "LZ4", "TAR.GZ"], font=font_style, state="readonly", width=15)format_dropdown.pack(pady=5)format_dropdown.current(0)compress_btn = tk.Button(root, text="Compress Files", command=compress_files, font=font_style, width=20)compress_btn.pack(pady=15)exit_btn = tk.Button(root, text="Exit", command=root.quit, font=font_style, width=20)exit_btn.pack(pady=5)root.mainloop()